mapper.select:
select *
from tbl_board
where board_type = #{type}
where board_type = #{type}
where board_type = #{type}
where board_type = #{type}
order by brd_id desc

mapper.update:
UPDATE TBL_BOARD
SET   BRD_TITLE = #{brdTitle}
	, BRD_CONTENT = #{brdContent}
	, BRD_TYPE = #{brdType}
	, BRD_IMAGE = #{brdImage}
WHERE BRD_ID = #{brdId}
