mapper.select:
select * from (select a.*, rownum as rnum from (select * from tbl_board where brd_type = #{type} and BRD_WRITER LIKE '%' || #{keyword} || '%' and BRD_TITLE LIKE '%' || #{keyword} || '%' and BRD_CONTENT LIKE '%' || #{keyword} || '%' and brd_date between to_date(#{keyword}, 'yyyy-MM-dd') and TO_CHAR(TO_DATE(#{keyword}, 'yyyy-MM-dd') + INTERVAL '1' DAY, 'YYYY-MM-DD') order by brd_id desc ) a
) b
where b.rnum > (#{page} - 1) * 10 and b.rnum <= (#{page} * 10)

mapper.update:
UPDATE TBL_BOARD
SET   BRD_TITLE = #{brdTitle}
	, BRD_CONTENT = #{brdContent}
WHERE BRD_ID = #{brdId}

mapper.select:
  		select count(1) 
		from tbl_board
		where brd_type = #{type}
